import { Component } from '@angular/core';
import { Camera, CameraOptions, PictureSourceType } from '@ionic-native/camera/ngx';
import * as Tesseract from 'tesseract.js';
import { HttpClient } from '@angular/common/http';
import { NavController, ActionSheetController, Platform, LoadingController, ToastController } from '@ionic/angular';
// import { NgProgress } from 'ngx-progressbar';
// import { File } from '@ionic-native/file/ngx';


@Component({
    selector: 'app-home',
    templateUrl: 'home.page.html',
    styleUrls: ['home.page.scss'],
})
export class HomePage {

    selectedImage: string;
    imageText: string;
    protected readonly tesseract;

    constructor(
        public navCtrl: NavController,
        private camera: Camera,
        private actionSheetCtrl: ActionSheetController,
        private loading: LoadingController,
        // public progress: NgProgress,
        public platform: Platform,
        private http : HttpClient) {


this.selectedImage = "data:image/png;base64,";

    const path = this.path();

    console.log('Hello OcrProvider', path + 'assets/lib/tesseract.js-');

    //alert(this.file.dataDirectory);
    this.tesseract = Tesseract.create({
      langPath: path + 'assets/lib/tesseract.js-',
      corePath: path + 'assets/lib/tesseract.js-core_0.1.0.js',
      workerPath: path + 'assets/lib/tesseract.js-worker_1.0.10.js',
    });

    
}

    async selectSource() {
        const actionSheet = await this.actionSheetCtrl.create({
            header: 'Select Source',
            buttons: [ 
                {
                    text: 'Capture Image',
                    role: 'camera',
                    icon: 'camera',
                    handler: () => {
                        return this.getPicture(this.camera.PictureSourceType.CAMERA);
                    }
                },
                {
                    text: 'Use Libary',
                    role: 'libary',
                    icon: 'folder-open',
                    handler: () => {
                        return this.getPicture(this.camera.PictureSourceType.PHOTOLIBRARY);
                    }
                }
            ]
        });
        await actionSheet.present();
    }


    getPicture(sourceType: PictureSourceType) {


        if (this.platform.is('cordova')) {

            this.camera.getPicture({
                quality: 100,
                destinationType: this.camera.DestinationType.DATA_URL,
                sourceType: sourceType,
                allowEdit: true,
                saveToPhotoAlbum: false,
                correctOrientation: true
            }).then((imageData) => {
                this.selectedImage = `data:image/jpeg;base64,${imageData}`;

                this.http.post('http://127.0.0.1:5000/uploadimage', {imageData}).subscribe(result => {
                    // get result here
                    console.log(result);
                });

            });
        } else {
            alert('cordova not available');
        }

    }


    async recognizeImage() {
        const loading = await this.loading.create({
            message: 'Recognizing...',
            spinner: 'lines', // lines, lines-small, dots, bubbles, circles, crescent
        });
        loading.present();

        this.tesseract.recognize(this.selectedImage)
            .progress(message => {
                console.log(message);

                if (message.status === 'recognizing text') {
                    // this.progress.set(message.progress);
                }
            })
            .catch(err => console.error(err))
            .then(result => {
                loading.dismiss();
                this.imageText = result.text;
                alert(this.imageText);
            })
            .finally(resultOrError => {
                loading.dismiss();
                // this.progress.complete();
                alert(resultOrError);

            });
    }
	private path(): string {
    if (this.platform.is('cordova')) {
      const href = window.location.href;

	const hashIndex = href.indexOf('#');

	const lastSlashIndex = href.substr(0, 
  	hashIndex > -1 ? hashIndex : null
	).lastIndexOf('/');

	const host = href.substr(0, 
  	lastSlashIndex > -1 ? lastSlashIndex : null 
	) + '/';

	return host;
    }

    // This path works in browser.
    return window.location.protocol + '//' + window.location.hostname
      + (window.location.port ? ':' + window.location.port : '')
      + '/';

    // This path works in simulator.
    // return '/android_assets/www/';
  }
}
